import React from 'react'
import Header from "./Header";
import Main from "./Main";
import PopupWithForm from "./PopupWithForm"
import ImagePopup from "./ImagePopup"

import api from "../utils/api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import {Route, Switch, Redirect, useHistory} from 'react-router-dom'

import Footer from "./Footer";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import Login from "./Login";
import Register from "./Register";
import ProtectedRoute from "./ProtectedRoute";
import InfoTooltip from "./InfoTooltip";
import * as Auth from '../utils/auth';


function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [isImagePopupOpen, setImagePopupOpen] = React.useState(false)
    const [isTooltipOpen, setIsTooltipOpen] = React.useState(false)

    const [selectedCard, setSelectedCard] = React.useState({});
    const [currentUser, setCurrentUser] = React.useState({});

    const [loggedIn, setLoggedIn] = React.useState(false)
    const [message, setMessage] = React.useState({iconPath: '', text: ''})
    const [email, setEmail] = React.useState('')
    const history = useHistory();

    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getInitialCards()
            .then(cardList => {
                setCards(cardList);
            })
            .catch((err) => {
                console.log(err)
            })
    }, [])

    function handleCardLike(card) {
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        api.changeLikeCardStatus(card._id, isLiked)
            .then((newCard) => {
                //const newCards = cards.map((currentCard) => currentCard._id === card._id ? newCard : currentCard)
                //setCards(newCards)
                setCards((cards) => cards.map((currentCard) => currentCard._id === card._id ? newCard : currentCard))

            })
            .catch((err) => {
                console.log(err)
            })
    }

    function handleCardDelete(card) {
        api.removeCard(card._id)
            .then(() => {
                const newCards = cards.filter((elem) => elem !== card);
                setCards(newCards);
            })
            .catch((err) => {
                console.log(err)
            })
    }

    React.useEffect(() => {
        api.getUserInfo()
            .then((data) => {
                setCurrentUser(data)
            })
            .catch((err) => {
                console.log(err)
            });
    }, [])

    React.useEffect(()=> {
        const jwt = localStorage.getItem('jwt');
        if (jwt) {
            Auth.getContent(jwt)
                .then((res) => {
                    setLoggedIn(true);
                    setEmail(res.data.email);
                    history.push('/')
                })
                .catch(err => console.log(err))
        }
    })

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true)
    }

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true)
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true)
    }

    function closeAllPopups() {
        setIsEditAvatarPopupOpen(false)
        setIsEditProfilePopupOpen(false)
        setIsAddPlacePopupOpen(false)
        setImagePopupOpen(false)
        setSelectedCard({})
        setIsTooltipOpen(false)
    }

    function handleCardClick(card) {
        setSelectedCard(card)
        setImagePopupOpen(true)
    }

    function handleUpdateUser({name, about}) {
        api.editUserInfo(name, about)
            .then((data) => {
                setCurrentUser(data);
                closeAllPopups();
            })
            .catch((err) => {
                console.log(err)
            })
    }

    function handleUpdateAvatar({avatar}) {
        api.editUserAvatar(avatar)
            .then((data) => {
                setCurrentUser(data);
                closeAllPopups();
            })
            .catch((err) => {
                console.log(err)
            })
    }

    function handleAddPlaceSubmit({name, link}) {
        api.addCard(name, link)
            .then((data) => {
                setCards([data, ...cards]);
                closeAllPopups()
            })
            .catch((err) => {
                console.log(err)
            })
    }

    function handleInfoTooltipOpen() {
        setIsTooltipOpen(true)
    }

    function handleInfoTooltipContent({iconPath, text}) {
        setMessage({iconPath: iconPath, text: text})
    }

    function handleSignOut() {
        setLoggedIn(false);
        localStorage.removeItem('jwt');
        setEmail('')
        history.push('sign-in')
    }

    return (

        <CurrentUserContext.Provider value={currentUser}>
            <div className="page">
                <div className="page__container">
                    <Header loggedIn={loggedIn} email={email} handleSignOut={handleSignOut}/>
                    <Switch>
                        <Route path="/sign-in">
                            <Login
                            openInfoTooltip={handleInfoTooltipOpen}
                            onClose={closeAllPopups}
                            infoTooltipContent={handleInfoTooltipContent}
                            setEmail={setEmail}
                            setLoggedIn={setLoggedIn}
                            />
                        </Route>
                        <Route path="/sign-up">
                            <Register
                                openTooltip={handleInfoTooltipOpen}
                                onClose={closeAllPopups}
                                infoTooltipContent={handleInfoTooltipContent}
                            />
                        </Route>
                        <ProtectedRoute path="/" component={Main} onEditProfile={handleEditProfileClick}
                                        onAddPlace={handleAddPlaceClick}
                                        onEditAvatar={handleEditAvatarClick} onCardClick={handleCardClick}
                                        onCardLike={handleCardLike} cards={cards}
                                        onCardDelete={handleCardDelete} loggedIn={loggedIn}>
                            {/*<Main />*/}
                        </ProtectedRoute>

                        <Route path="/">
                            {loggedIn ? <Redirect to="/main"/> : <Redirect to="/sign-in"/>}
                        </Route>
                    </Switch>
                    <Footer/>
                    <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}
                                      onUpdateUser={handleUpdateUser}/>
                    <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}
                                     onUpdateAvatar={handleUpdateAvatar}/>
                    <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}
                                   onAddPlace={handleAddPlaceSubmit}/>

                    <PopupWithForm
                        title="Вы уверены?"
                        name="remove-card"
                        buttonText="Да"
                        onClose={closeAllPopups}>
                    </PopupWithForm>
                    <ImagePopup
                        card={selectedCard}
                        onClose={closeAllPopups}
                        isOpen={isImagePopupOpen}/>
                    <InfoTooltip
                        isOpen={isTooltipOpen}
                        onClose={closeAllPopups}
                        message={message}
                    />
                </div>
            </div>
        </CurrentUserContext.Provider>

    );
}


export default App;
